// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "./FlashloanBase.sol";
import "./interfaces/IPool.sol";
import "./interfaces/IFlashLoanReceiver.sol";
import "./interfaces/IERC20.sol";

/**
 * @title AAVEFlashloan
 * @notice Contract for executing flashloans from AAVE with custom workflows
 */
contract AAVEFlashloan is FlashloanBase, IFlashLoanReceiver {
    /// @notice AAVE Pool address
    IPool public pool;

    /// @notice Flashloan operation data
    struct FlashloanOperation {
        address user;
        address token;
        uint256 amount;
        address[] workflows;
        bytes[] workflowData;
        bool executed;
    }

    /// @notice Current flashloan operation
    FlashloanOperation public currentOperation;

    /// @notice Custom errors
    error OnlyPool();
    error OperationNotInitialized();
    error OperationAlreadyExecuted();

    /**
     * @notice Initialize the contract
     * @param _owner The owner of the contract
     * @param _pool AAVE Pool address
     * @param _feeBps Initial fee in basis points
     * @param _minProfitBps Minimum profit required in basis points
     */
    function initialize(address _owner, address _pool, uint256 _feeBps, uint256 _minProfitBps) external initializer {
        __FlashloanBase_init(_owner, _feeBps, _minProfitBps);
        pool = IPool(_pool);
    }

    /**
     * @notice Execute flashloan from AAVE with multiple workflows
     * @param token The token to borrow
     * @param amount The amount to borrow
     * @param workflows Array of workflow contracts to execute in sequence
     * @param workflowData Array of custom data for each workflow
     * @dev First workflow's tokenIn must be the borrowed token
     * @dev Last workflow's tokenOut must be the borrowed token (for repayment)
     */
    function executeFlashloan(
        address token,
        uint256 amount,
        address[] calldata workflows,
        bytes[] calldata workflowData
    ) external nonReentrant whenNotPaused {
        _validateFlashloanParams(token, amount);
        if (workflows.length == 0) revert InvalidWorkflow();
        if (workflows.length != workflowData.length) revert InvalidWorkflow();

        // Store operation data
        currentOperation = FlashloanOperation({
            user: msg.sender,
            token: token,
            amount: amount,
            workflows: workflows,
            workflowData: workflowData,
            executed: false
        });

        // Prepare flashloan parameters
        address[] memory assets = new address[](1);
        assets[0] = token;

        uint256[] memory amounts = new uint256[](1);
        amounts[0] = amount;

        uint256[] memory modes = new uint256[](1);
        modes[0] = 0; // Don't open debt, just revert if funds can't be transferred

        bytes memory params = abi.encode(workflows, workflowData);

        // Execute flashloan
        pool.flashLoan(
            address(this),
            assets,
            amounts,
            modes,
            address(this),
            params,
            0 // referral code
        );

        // Clear operation data
        delete currentOperation;
    }

    /**
     * @notice Callback function called by AAVE Pool after flashloan
     * @param assets The addresses of the assets being flash-borrowed
     * @param amounts The amounts of the assets being flash-borrowed
     * @param premiums The fee of each asset being flash-borrowed
     * @param params Variadic packed params
     * @return True if execution succeeds
     */
    function executeOperation(
        address[] calldata assets,
        uint256[] calldata amounts,
        uint256[] calldata premiums,
        address, /* initiator */
        bytes calldata params
    ) external override returns (bool) {
        // Only AAVE Pool can call this
        if (msg.sender != address(pool)) revert OnlyPool();

        // Verify operation is initialized
        if (currentOperation.user == address(0)) revert OperationNotInitialized();
        if (currentOperation.executed) revert OperationAlreadyExecuted();

        address token = assets[0];
        uint256 amount = amounts[0];
        uint256 premium = premiums[0];

        // Mark as executed
        currentOperation.executed = true;

        // Decode workflow parameters
        (address[] memory workflows, bytes[] memory workflowDataBytes) = abi.decode(params, (address[], bytes[]));

        // Execute workflow chain
        uint256 finalAmount = _executeWorkflowChain(workflows, workflowDataBytes, token, amount);

        // Calculate profit: finalAmount - original amount
        // Profit must cover: premium (AAVE fee) + our fee + min profit
        uint256 profit = finalAmount > amount ? finalAmount - amount : 0;

        // Calculate total amount to repay (principal + premium)
        uint256 totalRepayAmount = amount + premium;

        // Check if we have enough to repay
        if (finalAmount < totalRepayAmount) revert InsufficientProfit();

        // Calculate fee on profit
        uint256 fee = _calculateFee(profit);

        // Calculate net profit after fees
        uint256 netProfit = profit > fee ? profit - fee : 0;

        // Validate profit after fees and premium
        // Net profit must be at least minProfitBps of the original amount
        _validateProfit(netProfit, amount);

        // Approve repayment to AAVE Pool
        IERC20(token).approve(address(pool), totalRepayAmount);

        // Calculate remaining amount after repayment
        uint256 remaining = finalAmount - totalRepayAmount;

        // Transfer profit (after fee) to user if any
        if (remaining > fee) {
            uint256 userProfit = remaining - fee;
            if (userProfit > 0) {
                IERC20(token).transfer(currentOperation.user, userProfit);
            }
        }

        // Transfer fee to contract
        if (fee > 0 && remaining >= fee) {
            IERC20(token).transfer(address(this), fee);
        }

        // Emit event
        emit FlashloanExecuted(currentOperation.user, token, amount, true, netProfit, fee);

        return true;
    }

    /**
     * @notice Update AAVE Pool address
     * @param _pool New pool address
     */
    function setPool(address _pool) external onlyOwner {
        pool = IPool(_pool);
    }
}

src/FlashloanBase.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import {UUPSUpgradeable} from "@openzeppelin/contracts/proxy/utils/UUPSUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "./utils/OwnableUpgradeable.sol";
import "./utils/PausableUpgradeable.sol";
import "./utils/ReentrancyGuardUpgradeable.sol";
import "./interfaces/IFlashloanWorkflow.sol";
import "./interfaces/IERC20.sol";

/**
 * @title FlashloanBase
 * @notice Base contract for flashloan operations with upgradability, security, and custom workflow support
 */
abstract contract FlashloanBase is
    Initializable,
    UUPSUpgradeable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable
{
    /// @notice Fee percentage in basis points (1 basis point = 0.01%)
    uint256 public feeBps;

    /// @notice Maximum fee percentage (1000 = 10%)
    uint256 public constant MAX_FEE_BPS = 1000;

    /// @notice Minimum profit required for successful execution (in basis points)
    uint256 public minProfitBps;

    /// @notice Mapping to track active flashloan operations
    mapping(bytes32 => bool) public activeFlashloans;

    /// @notice Events
    event FlashloanExecuted(
        address indexed user, address indexed token, uint256 amount, bool success, uint256 profit, uint256 fee
    );

    event FeeUpdated(uint256 oldFee, uint256 newFee);
    event MinProfitUpdated(uint256 oldMinProfit, uint256 newMinProfit);
    event WorkflowExecuted(address indexed workflow, address indexed tokenIn, address indexed tokenOut, uint256 amountIn, uint256 amountOut, bool success);

    /// @notice Custom errors
    error InvalidAmount();
    error InvalidToken();
    error InvalidWorkflow();
    error InsufficientProfit();
    error FlashloanFailed();
    error RepaymentFailed();
    error WorkflowExecutionFailed();
    error FeeTooHigh();
    error InvalidFee();
    error InvalidWorkflowChain();
    error WorkflowChainMismatch();

    /**
     * @notice Initialize the contract
     * @param _owner The owner of the contract
     * @param _feeBps Initial fee in basis points
     * @param _minProfitBps Minimum profit required in basis points
     */
    function __FlashloanBase_init(address _owner, uint256 _feeBps, uint256 _minProfitBps) internal onlyInitializing {
        __Ownable_init(_owner);
        __ReentrancyGuard_init();
        __Pausable_init();

        if (_feeBps > MAX_FEE_BPS) revert FeeTooHigh();
        feeBps = _feeBps;
        minProfitBps = _minProfitBps;
    }

    /**
     * @notice Set the fee percentage
     * @param _feeBps New fee in basis points
     */
    function setFee(uint256 _feeBps) external onlyOwner {
        if (_feeBps > MAX_FEE_BPS) revert FeeTooHigh();
        uint256 oldFee = feeBps;
        feeBps = _feeBps;
        emit FeeUpdated(oldFee, _feeBps);
    }

    /**
     * @notice Set the minimum profit required
     * @param _minProfitBps Minimum profit in basis points
     */
    function setMinProfit(uint256 _minProfitBps) external onlyOwner {
        uint256 oldMinProfit = minProfitBps;
        minProfitBps = _minProfitBps;
        emit MinProfitUpdated(oldMinProfit, _minProfitBps);
    }

    /**
     * @notice Pause the contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause the contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Execute a single workflow (tokenIn -> tokenOut)
     * @param workflow The workflow contract to execute
     * @param tokenIn The input token address
     * @param amountIn The amount of input tokens
     * @param data Custom data for the workflow (should encode tokenOut and swap parameters)
     * @return success Whether the workflow succeeded
     * @return amountOut The amount of output tokens received
     */
    function _executeWorkflow(address workflow, address tokenIn, uint256 amountIn, bytes memory data)
        internal
        returns (bool success, uint256 amountOut)
    {
        if (workflow == address(0)) revert InvalidWorkflow();

        // Execute the workflow
        (success, amountOut) = IFlashloanWorkflow(workflow).executeWorkflow(tokenIn, amountIn, data);

        // Decode tokenOut from data for event emission
        // Data format: (address tokenOut, uint256 minAmountOut, ...)
        address tokenOut = address(0);
        if (data.length >= 32) {
            // Decode first parameter (tokenOut)
            (tokenOut,) = abi.decode(data, (address, uint256));
        }

        emit WorkflowExecuted(workflow, tokenIn, tokenOut, amountIn, amountOut, success);

        if (!success) revert WorkflowExecutionFailed();
    }

    /**
     * @notice Execute multiple workflows in sequence
     * @dev Workflows must form a chain: workflow[i].tokenOut == workflow[i+1].tokenIn
     * @dev First workflow's tokenIn must be the borrowed token
     * @dev Last workflow's tokenOut must be the borrowed token (for repayment)
     * @param workflows Array of workflow contracts to execute
     * @param workflowData Array of data for each workflow
     * @param borrowedToken The token that was borrowed (must match first workflow's tokenIn and last workflow's tokenOut)
     * @param borrowedAmount The amount of tokens borrowed
     * @return finalAmount The final amount of borrowed tokens after all workflows
     */
    function _executeWorkflowChain(
        address[] memory workflows,
        bytes[] memory workflowData,
        address borrowedToken,
        uint256 borrowedAmount
    ) internal returns (uint256 finalAmount) {
        if (workflows.length == 0) revert InvalidWorkflowChain();
        if (workflows.length != workflowData.length) revert WorkflowChainMismatch();

        address currentToken = borrowedToken;
        uint256 currentAmount = borrowedAmount;

        // Store first workflow's tokenIn for validation
        address firstWorkflowTokenIn = borrowedToken;

        // Execute each workflow in sequence
        for (uint256 i = 0; i < workflows.length; i++) {
            // Decode tokenOut from workflow data
            // Data format: (address tokenOut, uint256 minAmountOut, ...)
            address tokenOut;
            if (workflowData[i].length >= 32) {
                (tokenOut,) = abi.decode(workflowData[i], (address, uint256));
            } else {
                revert InvalidWorkflowChain();
            }

            // Validate workflow chain
            if (i == 0) {
                // First workflow's tokenIn must be the borrowed token
                if (currentToken != borrowedToken) revert InvalidWorkflowChain();
            } else {
                // For subsequent workflows, tokenIn must match previous workflow's tokenOut
                // This is validated implicitly by using currentToken
            }

            if (i == workflows.length - 1) {
                // Last workflow's tokenOut must equal first workflow's tokenIn (borrowed token)
                // This ensures the chain returns to the original token for repayment
                if (tokenOut != firstWorkflowTokenIn) revert InvalidWorkflowChain();
            }

            // Execute workflow
            (bool success, uint256 amountOut) = _executeWorkflow(workflows[i], currentToken, currentAmount, workflowData[i]);

            if (!success) revert WorkflowExecutionFailed();

            // Update for next iteration
            currentToken = tokenOut;
            currentAmount = amountOut;
        }

        // Final amount should be in borrowed token
        if (currentToken != borrowedToken) revert InvalidWorkflowChain();
        finalAmount = currentAmount;
    }

    /**
     * @notice Calculate fee amount
     * @param amount The base amount
     * @return fee The calculated fee
     */
    function _calculateFee(uint256 amount) internal view returns (uint256 fee) {
        fee = (amount * feeBps) / 10000;
    }

    /**
     * @notice Validate flashloan parameters
     * @param token The token address
     * @param amount The amount to borrow
     */
    function _validateFlashloanParams(address token, uint256 amount) internal pure {
        if (token == address(0)) revert InvalidToken();
        if (amount == 0) revert InvalidAmount();
    }

    /**
     * @notice Check if profit is sufficient
     * @param profit The profit made
     * @param amount The original amount
     */
    function _validateProfit(uint256 profit, uint256 amount) internal view {
        uint256 minProfit = (amount * minProfitBps) / 10000;
        if (profit < minProfit) revert InsufficientProfit();
    }

    /**
     * @notice Authorize upgrade (UUPS pattern)
     */
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    /**
     * @notice Withdraw fees collected
     * @param token The token to withdraw
     * @param to The address to send fees to
     */
    function withdrawFees(address token, address to) external onlyOwner {
        IERC20 tokenContract = IERC20(token);
        uint256 balance = tokenContract.balanceOf(address(this));
        if (balance > 0) {
            tokenContract.transfer(to, balance);
        }
    }

    /**
     * @notice Emergency withdraw ETH
     */
    function emergencyWithdraw() external onlyOwner {
        (bool success,) = payable(owner()).call{value: address(this).balance}("");
        require(success, "Transfer failed");
    }
}

src/UniswapFlashSwap.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "./FlashloanBase.sol";
import "./interfaces/IUniswapV3Pool.sol";
import "./interfaces/IUniswapV3SwapCallback.sol";
import "./interfaces/IERC20.sol";

/**
 * @title UniswapFlashSwap
 * @notice Contract for executing flash swaps from Uniswap V3 with custom workflows
 */
contract UniswapFlashSwap is FlashloanBase, IUniswapV3SwapCallback {
    /// @notice Flash swap operation data
    struct FlashSwapOperation {
        address user;
        address tokenIn;
        address tokenOut;
        uint256 amountIn;
        address pool;
        address[] workflows;
        bytes[] workflowData;
        bool executed;
    }

    /// @notice Current flash swap operation
    FlashSwapOperation public currentOperation;

    /// @notice Custom errors
    error OnlyPool();
    error OperationNotInitialized();
    error OperationAlreadyExecuted();
    error InvalidPool();
    error InsufficientRepayment();

    /**
     * @notice Initialize the contract
     * @param _owner The owner of the contract
     * @param _feeBps Initial fee in basis points
     * @param _minProfitBps Minimum profit required in basis points
     */
    function initialize(address _owner, uint256 _feeBps, uint256 _minProfitBps) external initializer {
        __FlashloanBase_init(_owner, _feeBps, _minProfitBps);
    }

    /**
     * @notice Execute flash swap from Uniswap V3 with multiple workflows
     * @param pool The Uniswap V3 pool address
     * @param tokenIn The token to receive (flashloan)
     * @param tokenOut The token to pay back
     * @param amountIn The amount of tokenIn to receive
     * @param workflows Array of workflow contracts to execute in sequence
     * @param workflowData Array of custom data for each workflow
     * @dev First workflow's tokenIn must be the borrowed token (tokenIn)
     * @dev Last workflow's tokenOut must be the repayment token (tokenOut)
     */
    function executeFlashSwap(
        address pool,
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        address[] calldata workflows,
        bytes[] calldata workflowData
    ) external nonReentrant whenNotPaused {
        _validateFlashloanParams(tokenIn, amountIn);
        if (pool == address(0)) revert InvalidPool();
        if (workflows.length == 0) revert InvalidWorkflow();
        if (workflows.length != workflowData.length) revert InvalidWorkflow();

        IUniswapV3Pool poolContract = IUniswapV3Pool(pool);

        // Verify pool tokens
        address poolToken0 = poolContract.token0();
        address poolToken1 = poolContract.token1();
        require(
            (poolToken0 == tokenIn && poolToken1 == tokenOut) || (poolToken1 == tokenIn && poolToken0 == tokenOut),
            "Invalid pool tokens"
        );

        // Store operation data
        currentOperation = FlashSwapOperation({
            user: msg.sender,
            tokenIn: tokenIn,
            tokenOut: tokenOut,
            amountIn: amountIn,
            pool: pool,
            workflows: workflows,
            workflowData: workflowData,
            executed: false
        });

        // Determine swap direction
        bool zeroForOne = tokenIn == poolToken0;

        // Execute flash swap
        // Negative amount means exact output (we want to receive amountIn)
        poolContract.swap(
            address(this),
            zeroForOne,
            -int256(amountIn),
            zeroForOne ? 4295128739 : 1461446703485210103287273052203988822378723970342, // Max price
            abi.encode(workflows, workflowData)
        );

        // Clear operation data
        delete currentOperation;
    }

    /**
     * @notice Callback function called by Uniswap V3 Pool during swap
     * @param amount0Delta The amount of token0 that was sent (negative) or must be received (positive)
     * @param amount1Delta The amount of token1 that was sent (negative) or must be received (positive)
     * @param data Encoded workflow parameters
     */
    function uniswapV3SwapCallback(int256 amount0Delta, int256 amount1Delta, bytes calldata data) external override {
        // Verify operation is initialized
        if (currentOperation.user == address(0)) revert OperationNotInitialized();
        if (currentOperation.executed) revert OperationAlreadyExecuted();

        // Only the pool can call this
        if (msg.sender != currentOperation.pool) revert OnlyPool();

        // Determine which token we received and how much we need to pay back
        address tokenReceived;
        uint256 amountReceived;
        address tokenToPay;
        uint256 amountToPay;

        IUniswapV3Pool poolContract = IUniswapV3Pool(currentOperation.pool);
        address token0 = poolContract.token0();
        address token1 = poolContract.token1();

        // In Uniswap V3 callback:
        // - amount0Delta < 0 means we received token0 (negative delta = we received it)
        // - amount0Delta > 0 means we owe token0 (positive delta = we must pay it)
        // - amount1Delta < 0 means we received token1
        // - amount1Delta > 0 means we owe token1
        if (amount0Delta < 0) {
            // We received token0 (negative delta), need to pay token1
            tokenReceived = token0;
            amountReceived = uint256(-amount0Delta);
            tokenToPay = token1;
            amountToPay = uint256(amount1Delta);
        } else {
            // We received token1 (amount1Delta < 0), need to pay token0
            tokenReceived = token1;
            amountReceived = uint256(-amount1Delta);
            tokenToPay = token0;
            amountToPay = uint256(amount0Delta);
        }

        // Mark as executed
        currentOperation.executed = true;

        // Pre-approve pool for repayment (BEFORE workflow execution)
        // This ensures approval is available when pool checks it after callback returns
        // Use max approval for safety
        IERC20(tokenToPay).approve(currentOperation.pool, type(uint256).max);

        // Decode workflow parameters
        (address[] memory workflows, bytes[] memory workflowDataBytes) = abi.decode(data, (address[], bytes[]));

        // Execute workflows in sequence
        // For Uniswap: first workflow's tokenIn = tokenReceived (borrowed), last workflow's tokenOut = tokenToPay (repayment)
        address currentToken = tokenReceived;
        uint256 currentAmount = amountReceived;

        // Validate first workflow's tokenIn
        if (currentToken != tokenReceived) revert InvalidWorkflowChain();

        for (uint256 i = 0; i < workflows.length; i++) {
            // Decode tokenOut from workflow data
            address tokenOut;
            (tokenOut,) = abi.decode(workflowDataBytes[i], (address, uint256));

            // Validate last workflow's tokenOut
            if (i == workflows.length - 1) {
                // Last workflow's tokenOut must equal tokenToPay (repayment token)
                // Note: For Uniswap, tokenToPay may differ from tokenReceived
                // But we still validate that the chain ends with the correct repayment token
                if (tokenOut != tokenToPay) revert InvalidWorkflowChain();
            }

            // Execute workflow
            (bool success, uint256 amountOut) = _executeWorkflow(workflows[i], currentToken, currentAmount, workflowDataBytes[i]);
            if (!success) revert WorkflowExecutionFailed();

            currentToken = tokenOut;
            currentAmount = amountOut;
        }

        // Final token must be tokenToPay (repayment token)
        if (currentToken != tokenToPay) revert InvalidWorkflowChain();

        // Check if we have enough tokens to repay
        if (currentAmount < amountToPay) revert InsufficientRepayment();

        // Process profit and transfers
        _processUniswapProfitAndEmit(currentAmount, amountToPay, amountReceived, tokenReceived, tokenToPay);
    }

    /**
     * @notice Process profit calculation and transfers for Uniswap flash swap
     * @param finalAmount Final amount of tokenToPay after workflows
     * @param amountToPay Amount needed to repay the pool
     * @param amountReceived Original amount received from pool
     * @param tokenReceived Token received from pool
     * @param tokenToPay Token to pay back to pool
     */
    function _processUniswapProfitAndEmit(
        uint256 finalAmount,
        uint256 amountToPay,
        uint256 amountReceived,
        address tokenReceived,
        address tokenToPay
    ) internal {
        uint256 profit = finalAmount - amountToPay;
        uint256 fee = _calculateFee(profit);
        uint256 netProfit = profit > fee ? profit - fee : 0;
        _validateProfit(netProfit, amountReceived);

        address user = currentOperation.user;
        if (profit > fee) {
            IERC20(tokenToPay).transfer(user, profit - fee);
            if (fee > 0) {
                IERC20(tokenToPay).transfer(address(this), fee);
            }
        }

        emit FlashloanExecuted(user, tokenReceived, amountReceived, true, netProfit, fee);
    }
}

src/examples/SimpleSwapWorkflow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "../interfaces/IFlashloanWorkflow.sol";
import "../interfaces/IERC20.sol";

/**
 * @title SimpleSwapWorkflow
 * @notice Example workflow that swaps tokens using a DEX
 */
contract SimpleSwapWorkflow is IFlashloanWorkflow {
    /// @notice DEX router interface (simplified)
    address public router;

    /// @notice Swap function signature
    bytes4 private constant SWAP_SELECTOR = bytes4(keccak256("swap(address,address,uint256,uint256,address)"));

    event SwapExecuted(address tokenIn, address tokenOut, uint256 amountIn, uint256 amountOut);

    constructor(address _router) {
        router = _router;
    }

    /**
     * @notice Execute a single swap workflow (tokenIn -> tokenOut)
     * @param tokenIn The input token address
     * @param amountIn The amount of input tokens
     * @param data Encoded swap parameters: (address tokenOut, uint256 minAmountOut)
     * @return success Whether the swap succeeded
     * @return amountOut The amount of output tokens received
     */
    function executeWorkflow(address tokenIn, uint256 amountIn, bytes calldata data)
        external
        override
        returns (bool success, uint256 amountOut)
    {
        // Decode swap parameters
        (address tokenOut, uint256 minAmountOut) = abi.decode(data, (address, uint256));

        // Approve router to spend input tokens
        IERC20(tokenIn).approve(router, amountIn);

        // Execute swap (simplified - in production, use proper DEX router)
        // This is a placeholder - actual implementation depends on the DEX
        (bool swapSuccess, bytes memory returnData) =
            router.call(abi.encodeWithSelector(SWAP_SELECTOR, tokenIn, tokenOut, amountIn, minAmountOut, address(this)));

        if (!swapSuccess) {
            return (false, 0);
        }

        // Get amount out (simplified)
        amountOut = abi.decode(returnData, (uint256));

        // Check if minimum amount out is met
        if (amountOut >= minAmountOut) {
            success = true;
        } else {
            success = false;
            amountOut = 0;
        }

        emit SwapExecuted(tokenIn, tokenOut, amountIn, amountOut);
    }
}

